mapper.select:
 select *  from yqs_good

 and id<#{startId,jdbcType=INTEGER}
 and academy =#{academy,jdbcType=CHAR}
 and ( goodDescribe like concat('%',#{keyword,jdbcType=CHAR},'%') 
 	or name like concat('%',#{keyword,jdbcType=CHAR},'%') )


 order by id desc

		limit #{range,jdbcType=INTEGER}
		limit 15
